package src;

public class Service {
	String name ;
	double hourlyCost;
	
	public Service ( String name , double hourlyCost) {
		this.name = name ;
		this.hourlyCost = hourlyCost ;
	}

	public double getHourlyCost() {
		// TODO Auto-generated method stub
		return this.hourlyCost;
	}

	public String getName() {
		// TODO Auto-generated method stub
		return this.name;
	}
	
	
}
